"use client";

import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Card, CardContent } from "@/components/ui/card";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Button } from "@/components/ui/button";
import {
  ChevronDown,
  ChevronUp,
  Code,
  Palette,
  Lightbulb,
  Rocket,
} from "lucide-react";

interface TeamMember {
  name: string;
  role: string;
  avatar: string;
  bio: string;
  skills: string[];
}

const teamMembers: TeamMember[] = [
  {
    name: "Prathamesh Chougale",
    role: "Full Stack Developer",
    avatar: "/api/placeholder/100/100",
    bio: "Passionate about creating scalable and efficient web solutions.",
    skills: ["React", "Node.js", "GraphQL", "AWS"],
  },
  {
    name: "Samantha Lee",
    role: "UX/UI Designer",
    avatar: "/api/placeholder/100/100",
    bio: "Dedicated to crafting intuitive and delightful user experiences.",
    skills: ["Figma", "Adobe XD", "User Research", "Prototyping"],
  },
  {
    name: "Raj Patel",
    role: "AI Specialist",
    avatar: "/api/placeholder/100/100",
    bio: "Exploring the frontiers of artificial intelligence and machine learning.",
    skills: ["TensorFlow", "PyTorch", "NLP", "Computer Vision"],
  },
  {
    name: "Elena Rodriguez",
    role: "Product Manager",
    avatar: "/api/placeholder/100/100",
    bio: "Bridging the gap between user needs and technical possibilities.",
    skills: ["Agile", "User Stories", "Roadmapping", "Data Analysis"],
  },
];

const AboutPage: React.FC = () => {
  const [activeSection, setActiveSection] = useState<string | null>(null);

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2,
      },
    },
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        type: "spring",
        stiffness: 100,
      },
    },
  };

  const sectionVariants = {
    hidden: { height: 0, opacity: 0 },
    visible: { height: "auto", opacity: 1, transition: { duration: 0.5 } },
  };

  const floatingIconVariants = {
    hidden: { y: 0 },
    visible: {
      y: [-10, 10, -10],
      transition: { repeat: Infinity, duration: 3 },
    },
  };

  const Section: React.FC<{
    title: string;
    icon: React.ReactNode;
    children: React.ReactNode;
  }> = ({ title, icon, children }) => (
    <motion.div variants={itemVariants} className="mb-8">
      <Button
        variant="outline"
        className="w-full justify-between text-lg font-semibold py-4"
        onClick={() => setActiveSection(activeSection === title ? null : title)}
      >
        <span className="flex items-center">
          {icon}
          <span className="ml-2">{title}</span>
        </span>
        {activeSection === title ? (
          <ChevronUp size={24} />
        ) : (
          <ChevronDown size={24} />
        )}
      </Button>
      <AnimatePresence>
        {activeSection === title && (
          <motion.div
            variants={sectionVariants}
            initial="hidden"
            animate="visible"
            exit="hidden"
            className="mt-4 bg-secondary p-6 rounded-lg"
          >
            {children}
          </motion.div>
        )}
      </AnimatePresence>
    </motion.div>
  );

  return (
    <div className="container mx-auto px-4 py-16 relative overflow-hidden">
      <motion.div
        className="absolute inset-0 -z-10 opacity-10"
        animate={{
          backgroundImage: [
            "radial-gradient(circle at 20% 20%, #ff6b6b 0%, transparent 50%)",
            "radial-gradient(circle at 80% 80%, #4ecdc4 0%, transparent 50%)",
            "radial-gradient(circle at 50% 50%, #45b7d1 0%, transparent 50%)",
            "radial-gradient(circle at 20% 20%, #ff6b6b 0%, transparent 50%)",
          ],
        }}
        transition={{ repeat: Infinity, duration: 20 }}
      />

      <motion.div
        className="relative"
        initial="hidden"
        animate="visible"
        variants={containerVariants}
      >
        <motion.h1
          variants={itemVariants}
          className="text-6xl font-bold mb-8 text-center bg-clip-text text-transparent bg-gradient-to-r from-primary to-secondary"
        >
          Innovate. Create. Inspire.
        </motion.h1>

        <motion.p
          variants={itemVariants}
          className="text-xl text-center mb-16 max-w-2xl mx-auto"
        >
          We are a team of visionaries, innovators, and problem-solvers
          dedicated to shaping the future of technology.
        </motion.p>

        <Section
          title="Our Expertise"
          icon={<Code size={24} className="text-primary" />}
        >
          <p className="mb-4">
            With a diverse skill set spanning full-stack development, AI, and UX
            design, we tackle complex challenges with creativity and precision.
            Our expertise in cutting-edge technologies allows us to build
            scalable, efficient, and user-friendly solutions that drive business
            growth.
          </p>
        </Section>

        <Section
          title="Our Approach"
          icon={<Palette size={24} className="text-primary" />}
        >
          <p className="mb-4">
            We believe in a collaborative, iterative approach that puts user
            needs at the forefront. By combining agile methodologies with design
            thinking, we ensure that our solutions not only meet technical
            requirements but also delight users and exceed expectations.
          </p>
        </Section>

        <Section
          title="Our Vision"
          icon={<Lightbulb size={24} className="text-primary" />}
        >
          <p className="mb-4">
            We envision a world where technology empowers individuals and
            businesses to reach their full potential. Our mission is to be at
            the forefront of this transformation, creating innovative solutions
            that make a lasting positive impact on society and the environment.
          </p>
        </Section>

        <motion.h2
          variants={itemVariants}
          className="text-4xl font-bold mb-8 text-center mt-16"
        >
          Meet Our Team
        </motion.h2>

        <motion.div
          variants={itemVariants}
          className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8"
        >
          {teamMembers.map((member, index) => (
            <motion.div
              key={index}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <Card className="overflow-hidden h-full">
                <CardContent className="p-6 flex flex-col h-full">
                  <motion.div
                    className="mb-4 relative"
                    whileHover={{ rotate: [0, -10, 10, -10, 0] }}
                    transition={{ duration: 0.5 }}
                  >
                    <Avatar className="w-24 h-24 mx-auto">
                      <AvatarImage src={member.avatar} alt={member.name} />
                      <AvatarFallback>
                        {member.name
                          .split(" ")
                          .map((n) => n[0])
                          .join("")}
                      </AvatarFallback>
                    </Avatar>
                    <motion.div
                      className="absolute -top-2 -right-2 bg-primary text-white dark:text-black rounded-full p-2"
                      variants={floatingIconVariants}
                    >
                      <Rocket size={16} />
                    </motion.div>
                  </motion.div>
                  <h3 className="text-xl font-semibold mb-1 text-center">
                    {member.name}
                  </h3>
                  <p className="text-sm text-gray-500 mb-4 text-center">
                    {member.role}
                  </p>
                  <p className="text-sm mb-4 flex-grow">{member.bio}</p>
                  <div className="flex flex-wrap gap-2 justify-center">
                    {member.skills.map((skill, skillIndex) => (
                      <span
                        key={skillIndex}
                        className="bg-secondary text-secondary-foreground text-xs px-2 py-1 rounded-full"
                      >
                        {skill}
                      </span>
                    ))}
                  </div>
                </CardContent>
              </Card>
            </motion.div>
          ))}
        </motion.div>
      </motion.div>
    </div>
  );
};

export default AboutPage;

// app/api/send-email/route.ts

import {  currentUser } from '@clerk/nextjs/server'
import { NextResponse } from 'next/server';
import nodemailer from 'nodemailer';

export async function POST(request: Request) {
  try {
    const { subject, name, message } = await request.json();
    const adminEmail = process.env.EMAIL_USER;
    const user = await currentUser();
    if(!user || !user.primaryEmailAddress) {
        return NextResponse.json({ message: 'User not found' }, { status: 404 });
    }
    const useremail = user.primaryEmailAddress?.emailAddress;
    // Create a transporter object using SMTP transport
    const transporter = nodemailer.createTransport({
      host: 'smtp.gmail.com', // Replace with your SMTP server host
      port: 587, // Replace with your SMTP server port
      secure: false, // Use true for port 465, false for other ports
      auth: {
        user: process.env.EMAIL_USER, // Your email user
        pass: process.env.EMAIL_PASS, // Your email password
      },
    });

    // Setup email data
    const mailOptions = {
      from: adminEmail, // Sender address
      to: useremail, // List of recipients
      subject: "Thank you reaching our Site", // Subject line
      html: getUserEmailTemplate(name), // HTML body
    };

    const mailOptionsAdmin = {
        from: useremail, // Sender address
        to: adminEmail, // List of recipients
        subject: subject, // Subject line
        html: getCompanyEmailTemplate(name, useremail, message), // HTML body
        };


    // Send mail
    await transporter.sendMail(mailOptions);
    await transporter.sendMail(mailOptionsAdmin);

    return NextResponse.json({ message: 'Email sent successfully' }, { status: 200 });
  } catch (error) {
    console.error('Error sending email:', error);
    return NextResponse.json({ message: 'Failed to send email' }, { status: 500 });
  }
}

function getUserEmailTemplate(name: string): string {
    return `
      <!DOCTYPE html>
      <html>
        <head>
          <style>
            body { font-family: Arial, sans-serif; line-height: 1.6; color: #333; }
            .container { max-width: 600px; margin: 0 auto; padding: 20px; }
            .header { background-color: #4299E1; color: white; padding: 20px; text-align: center; }
            .content { padding: 20px; background-color: #f8f8f8; }
            .footer { text-align: center; margin-top: 20px; font-size: 0.8em; color: #666; }
          </style>
        </head>
        <body>
          <div class="container">
            <div class="header">
              <h1>Thank You for Contacting {company name}</h1>
            </div>
            <div class="content">
              <p>Dear ${name},</p>
              <p>Thank you for reaching out to {company name}. We have received your message and appreciate your interest in our services.</p>
              <p>Our team will review your inquiry and get back to you as soon as possible, usually within 1-2 business days.</p>
              <p>In the meantime, feel free to explore our website for more information about our services and available chefs.</p>
              <p>Best regards,<br>The {company name} Team</p>
            </div>
            <div class="footer">
              <p>&copy; 2024 {company name}. All rights reserved.</p>
            </div>
          </div>
        </body>
      </html>
    `;
  }
  
  function getCompanyEmailTemplate(name: string, email: string, message: string): string {
    return `
      <!DOCTYPE html>
      <html>
        <head>
          <style>
            body { font-family: Arial, sans-serif; line-height: 1.6; color: #333; }
            .container { max-width: 600px; margin: 0 auto; padding: 20px; }
            .header { background-color: #4299E1; color: white; padding: 20px; text-align: center; }
            .content { padding: 20px; background-color: #f8f8f8; }
            .footer { text-align: center; margin-top: 20px; font-size: 0.8em; color: #666; }
          </style>
        </head>
        <body>
          <div class="container">
            <div class="header">
              <h1>New Contact Form Submission</h1>
            </div>
            <div class="content">
              <p><strong>Name:</strong> ${name}</p>
              <p><strong>Email:</strong> ${email}</p>
              <p><strong>Message:</strong></p>
              <p>${message}</p>
            </div>
            <div class="footer">
              <p>&copy; 2024 {company name}. All rights reserved.</p>
            </div>
          </div>
        </body>
      </html>
    `;
  }

"use client";

import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Card, CardContent } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Button } from "@/components/ui/button";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Mail, User, MessageSquare, Send } from "lucide-react";
import ContactPageAnimation from "@/components/svg/contactsvg";
import { useToast } from "@/hooks/use-toast";

const contactFormSchema = z.object({
  name: z.string().min(2, { message: "Name must be at least 2 characters" }),
  subject: z
    .string()
    .min(3, { message: "Subject must be at least 3 characters" }),
  message: z
    .string()
    .min(10, { message: "Message must be at least 10 characters" }),
});

type ContactFormData = z.infer<typeof contactFormSchema>;

const ContactPage: React.FC = () => {
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isSubmitted, setIsSubmitted] = useState(false);
  const { toast } = useToast();

  const {
    register,
    handleSubmit,
    formState: { errors },
    reset,
  } = useForm<ContactFormData>({
    resolver: zodResolver(contactFormSchema),
  });

  const onSubmit = async (data: ContactFormData) => {
    try {
      setIsSubmitting(true);
      await fetch("/api/contact", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(data),
      });
      setIsSubmitted(true);
      toast({
        title: "Message Sent!",
        description: "We'll get back to you soon.",
      });
      reset();
    } catch (error) {
      console.error(error);
      toast({
        variant: "destructive",
        title: "Failed to send message",
        description: "Please try again later.",
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  // const svgVariants = {
  //   hidden: { opacity: 0, pathLength: 0 },
  //   visible: {
  //     opacity: 1,
  //     pathLength: 1,
  //     transition: { duration: 2, ease: "easeInOut" },
  //   },
  // };

  return (
    <div className="min-h-screen w-full flex items-center justify-center p-4 overflow-hidden">
      <div className="max-w-7xl w-full flex flex-col lg:flex-row items-center justify-between">
        <motion.div
          className="lg:w-1/2 mb-8 lg:mb-0"
          initial={{ opacity: 0, x: -100 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8 }}
        >
          <h1 className="text-5xl lg:text-7xl font-bold mb-6 leading-tight">
            Let&apos;s Connect <br /> and Create
          </h1>
          <p className="text-xl text-black/80 dark:text-white/80 mb-8">
            Reach out and let&apos;s turn your ideas into reality.
          </p>
          <ContactPageAnimation />
        </motion.div>

        <motion.div
          className="lg:w-1/2"
          initial={{ opacity: 0, x: 100 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8, delay: 0.2 }}
        >
          <Card className="bg-white/10 backdrop-blur-lg shadow-xl w-full max-w-md mx-auto">
            <CardContent className="p-6">
              <AnimatePresence mode="wait">
                {isSubmitted ? (
                  <motion.div
                    key="success"
                    initial={{ opacity: 0, y: 50 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: -50 }}
                    className="text-center py-8"
                  >
                    <motion.div
                      initial={{ scale: 0 }}
                      animate={{ scale: 1 }}
                      transition={{
                        type: "spring",
                        stiffness: 500,
                        damping: 30,
                      }}
                      className="w-16 h-16 bg-green-500 rounded-full flex items-center justify-center mx-auto mb-4"
                    >
                      <Send size={24} />
                    </motion.div>
                    <h2 className="text-2xl font-bold mb-2">Message Sent!</h2>
                    <p className="text-white/80">
                      We&apos;ll get back to you soon.
                    </p>
                  </motion.div>
                ) : (
                  <motion.form
                    key="form"
                    onSubmit={handleSubmit(onSubmit)}
                    className="space-y-6"
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    exit={{ opacity: 0 }}
                  >
                    <div>
                      <label
                        htmlFor="name"
                        className="block text-sm font-medium mb-1"
                      >
                        Name
                      </label>
                      <div className="relative">
                        <User
                          className="absolute left-3 top-1/2 transform -translate-y-1/2 text-black/50 dark:text-white/50"
                          size={18}
                        />
                        <Input
                          id="name"
                          {...register("name")}
                          className="bg-white/20 placeholder-white/50 pl-10 focus:ring-2 focus:ring-purple-500 transition-all duration-300"
                          placeholder="Your name"
                        />
                      </div>
                      {errors.name && (
                        <motion.p
                          initial={{ opacity: 0, y: -10 }}
                          animate={{ opacity: 1, y: 0 }}
                          className="text-red-300 text-sm mt-1"
                        >
                          {errors.name.message}
                        </motion.p>
                      )}
                    </div>
                    <div>
                      <label
                        htmlFor="subject"
                        className="block text-sm font-medium mb-1"
                      >
                        Subject
                      </label>
                      <div className="relative">
                        <Mail
                          className="absolute left-3 top-1/2 transform -translate-y-1/2 text-black/50 dark:text-white/50"
                          size={18}
                        />
                        <Input
                          id="subject"
                          type="subject"
                          {...register("subject")}
                          className="bg-white/20 placeholder-white/50 pl-10 focus:ring-2 focus:ring-purple-500 transition-all duration-300"
                          placeholder="your@subject.com"
                        />
                      </div>
                      {errors.subject && (
                        <motion.p
                          initial={{ opacity: 0, y: -10 }}
                          animate={{ opacity: 1, y: 0 }}
                          className="text-red-300 text-sm mt-1"
                        >
                          {errors.subject.message}
                        </motion.p>
                      )}
                    </div>
                    <div>
                      <label
                        htmlFor="message"
                        className="block text-sm font-medium mb-1"
                      >
                        Message
                      </label>
                      <div className="relative">
                        <MessageSquare
                          className="absolute left-3 top-3 text-black/50 dark:text-white/50"
                          size={18}
                        />
                        <Textarea
                          id="message"
                          {...register("message")}
                          className="bg-white/20 placeholder-white/50 pl-10 focus:ring-2 focus:ring-purple-500 transition-all duration-300"
                          placeholder="Your message"
                          rows={4}
                        />
                      </div>
                      {errors.message && (
                        <motion.p
                          initial={{ opacity: 0, y: -10 }}
                          animate={{ opacity: 1, y: 0 }}
                          className="text-red-300 text-sm mt-1"
                        >
                          {errors.message.message}
                        </motion.p>
                      )}
                    </div>
                    <motion.div
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                    >
                      <Button
                        type="submit"
                        disabled={isSubmitting}
                        className="w-full bg-gradient-to-r from-pink-500 to-purple-500 hover:from-pink-600 hover:to-purple-600 text-white font-semibold py-2 px-4 rounded-md transition duration-300 ease-in-out"
                      >
                        {isSubmitting ? "Sending..." : "Send Message"}
                      </Button>
                    </motion.div>
                  </motion.form>
                )}
              </AnimatePresence>
            </CardContent>
          </Card>
        </motion.div>
      </div>
    </div>
  );
};

export default ContactPage;

@tailwind base;
@tailwind components;
@tailwind utilities;

:root::-webkit-scrollbar {
  display: none;
}

:root {
  --foreground-rgb: 0, 0, 0;
  --background-start-rgb: 214, 219, 220;
  --background-end-rgb: 255, 255, 255;
  -ms-overflow-style: none;  /* IE and Edge */
  scrollbar-width: none;  /* Firefox */
  user-select: none;
}

@media (prefers-color-scheme: dark) {
  :root {
    --foreground-rgb: 255, 255, 255;
    --background-start-rgb: 0, 0, 0;
    --background-end-rgb: 0, 0, 0;
  }
}

@layer utilities {
  .text-balance {
    text-wrap: balance;
  }
}

@layer base {
  :root {
    --background: 0 0% 100%;
    --foreground: 0 0% 3.9%;
    --card: 0 0% 100%;
    --card-foreground: 0 0% 3.9%;
    --popover: 0 0% 100%;
    --popover-foreground: 0 0% 3.9%;
    --primary: 0 0% 9%;
    --primary-foreground: 0 0% 98%;
    --secondary: 0 0% 96.1%;
    --secondary-foreground: 0 0% 9%;
    --muted: 0 0% 96.1%;
    --muted-foreground: 0 0% 45.1%;
    --accent: 0 0% 96.1%;
    --accent-foreground: 0 0% 9%;
    --destructive: 0 84.2% 60.2%;
    --destructive-foreground: 0 0% 98%;
    --border: 0 0% 89.8%;
